        <script>
            document.body.addEventListener('click', async (e) => {
                const trigger = e.target.closest("[data-area-view]");
                if (trigger) {
                    e.preventDefault();
                    const dataURL = trigger.getAttribute("data-area-view");
                    try {
                        const response = await fetch(dataURL);
                        if (!response.ok) throw new Error('Network response was not ok');
                        const data = await response.text();

                        trigger.closest(".view").querySelector(".area-column").innerHTML = data;
                    } catch (error) {
                        console.log("something went wrong");
                    }        
                }
            });
            function activeZoomedView(view) {
                document.body.classList.add('show-zoomed-view')
                document.querySelector(".places-area").setAttribute("data-view", view);
            }
            function closeZoomedView() {
                document.body.classList.remove("show-zoomed-view");
                document.querySelector('.places-area').classList.remove('show-left-col')
            }
            document.addEventListener("DOMContentLoaded", function() {
                const gallery_thumbnails = new Swiper(".gallery-modal .thumbnails", {
                    spaceBetween: 24,
                    slidesPerView: 4,
                    loop: true,
                    freeMode: true,
                    watchSlidesProgress: true,
                });

                const gallery_main_preview = new Swiper(".gallery-modal .main-preview .swiper", {
                    spaceBetween: 0,
                    slidesPerView: 1,
                    loop: true,
                    thumbs: {
                        swiper: gallery_thumbnails
                    },
                    navigation: {
                        nextEl: ".gallery-modal .arrows .next",
                        prevEl: ".gallery-modal .arrows .prev",
                    }
                });
            });
        </script>
